require 'rails_helper'

describe "post_show画面のテスト", type: :system, js: true do
  TEST_IMAGE_DATA="data:image/png;base64,"
  
  before do
    @user = FactoryBot.create(:lily)
    @post = FactoryBot.create(:post,image_data_uri:TEST_IMAGE_DATA,user: @user)
    sign_in @user
    visit post_path(@post)
  end
  describe "投稿消去" do
    it "消去される" do
      expect{click_button "消去する"}.to change(@user.posts, :count).by(-1)
    end
  end
  describe "user詳細pageに遷移" do
    it "user名をクリックで遷移する" do
      click_link @user.username
      expect(current_path).to eq user_path(@user)
    end
  end
end