require 'rails_helper'
describe "いいね機能", type: :system do
  before do
    TEST_IMAGE_DATA="data:image/png;base64,"

    @user = FactoryBot.create(:lily)
    @other_user = FactoryBot.create(:bob)
    @post = FactoryBot.create(:post,image_data_uri:TEST_IMAGE_DATA, user: @other_user)
    sign_in @user
    visit post_path(@post)
  end
  context "まだ「いいね」していない場合" do
    it "「いいね」できること" do
      expect {
        click_link "いいね"
      }.to change(Like, :count).by(1)
      expect(page).to have_link 'いいね済'
    end
  end
  context "「いいね済」の場合" do
    before do
      click_link "いいね"
    end
    it "「いいね」解除" do
      expect {
        click_link "いいね済"
      }.to change(Like, :count).by(-1)
      expect(page).to have_link 'いいね'
    end
  end
end